generated.css:
* {
  box-sizing: border-box;
  color: #ffffff;
  margin: 0;
}
html {
  height: 100%;
}
body {
  background: #ffccd0;
  margin: 0;
  font-family: "Amatic SC", cursive;
  display: flex;
  flex-direction: column;
  min-height: 100vh;
}
.main-content {
  padding-bottom: 100px;
}
.search {
  padding: 15px;
  border-radius: 5px;
  background-color: #fd4a56e8;
  margin: 110px auto 0;
  width: 740px;
  box-shadow: 1px 10px 30px #656565;
}

.details,
.search-params {
  width: 55%;
  padding: 15px;
  border-radius: 5px;
  background-color: #fd4a56e8;
  margin: 0 auto;
}

.details {
  width: 740px;
}

.pet {
  width: 100%;
  height: 160px;
  display: block;
  overflow: hidden;
  margin: 15px 0;
  background: #ffffff26;
  padding: 20px 0;
  border-radius: 3px;
}

.pet img {
  width: 100px;
  min-height: 100px;
  clip-path: circle(50% at 50% 50%);
}

.info {
  float: left;
  height: 100px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
}

.image-container {
  width: 100px;
  height: 100px;
  float: left;
  margin-right: 40px;
  padding: 0 20px;
}

.pet h1 {
  white-space: nowrap;
  font-weight: bold;
  width: 100%;
  overflow: hidden;
  margin: 0;
  font-size: 2.4em;
}

.pet h2 {
  white-space: nowrap;
  font-weight: normal;
  font-size: 20px;
  margin: 0;
  font-size: 1.8em;
}
.close {
  text-decoration: none;
  font-size: 2em;
  justify-content: right;
}

header a {
  color: #f2af29;
  font-size: 50px;
  text-decoration: none;
  font-weight: bold;
  display: block;
}

header {
  display: flex;
  align-content: center;
  justify-content: space-between;
  margin-bottom: 20px;
  padding: 20px;
  height: 300px;
  background-image: linear-gradient(
    to right,
    #c51d93,
    #f22e70,
    #ff5e4b,
    #ff9226,
    #ebc212
  );
  border-bottom: 1px solid #d1d1d1;
  box-shadow: 1px 4px 20px #1e8be945;
}

header .logo {
  background-size: contain;
  width: 160px;
  height: 120px;
  margin: 60px;
}

header h3 {
  color: #fff;
  font-size: 1em;
  font-weight: 100;
}

footer {
  height: 100px;
  width: 100%;
  background-image: linear-gradient(
    to right,
    #c51d93,
    #f22e70,
    #ff5e4b,
    #ff9226,
    #ebc212
  );
  box-shadow: -12px -12px 20px 5px #e5e2e06b;
  margin: auto auto 0 auto;
  padding: 40px 0;
  border-top: 1px solid #fff;
  text-align: center;
  font-size: 1.6em;
}

.item {
  width: 260px;
  height: 200px;
  padding: 22px 0;
  line-height: 1.7em;
  border: 1px solid #ffffff69;
  box-shadow: 1px 1px 71px #ffffff21;
  text-align: center;
  color: #fff;
  font-size: 3em;
  justify-content: space-around;
  border-radius: 4px;
  margin: 20px auto;
}

.details p {
  line-height: 2;
}

.details h1,
.details h2 {
  text-align: center;
}

.carousel {
  display: flex;
  justify-content: space-around;
  align-items: center;
  height: 400px;
}

.carousel > img {
  max-width: 45%;
  max-height: 400px;
}

.carousel-smaller {
  width: 50%;
}

.carousel-smaller > img {
  width: 100px;
  height: 100px;
  border-radius: 50%;
  display: inline-block;
  margin: 15px;
  cursor: pointer;

  border: 1px solid #ffffff69;
  box-shadow: 1px 1px 71px #ffffff21;

  transition: border 250ms;
}

.carousel-smaller > img.active {
  border: 20px solid #ffffff69;
  box-shadow: 1px 10px 71px #ffffff21;
}

.search-params label {
  display: block;
  width: 60px;
  font-size: 1.8em;
  width: 140px;
}

.search-params input,
.search-params select {
  margin-bottom: 30px;
  font-size: 18px;
  height: 40px;
  width: 325px;
  color: #ff4952;
  border-radius: 5px;
  border: none;
  padding: 0 5px;
}
.search-params input {
  color: #ff4952;
}

.search-params button,
#modal button,
.details button,
button {
  background-color: #f93b60;
  padding: 15px 45px;
  color: white;
  font-size: 18px;
  border: #eeeeee69 1px solid;
  border-radius: 3px;
  display: block;
  margin: 0 auto;
  cursor: pointer;
  transition: background 300ms;
  transition: border 500ms;
  display: inline-block;
  margin: 0 12px;
}

.search-params button:hover,
button:hover {
  background: #fb303a;
  border: 1px solid #fff;
}

.search-params button:active {
  background-color: #5f1d22;
}

.search-params button:focus {
  border: 1px solid cornflowerblue;
}

#modal {
  background-color: rgba(0, 0, 0, 0.9);
  position: fixed;
  left: 0;
  right: 0;
  bottom: 0;
  top: 0;
  z-index: 10;
  display: flex;
  justify-content: center;
  align-items: center;
}

#modal:empty {
  display: none;
}
#model h1 {
  color: #ff4952;
}

#modal > div {
  background-color: #ff4952;
  max-width: 500px;
  padding: 15px;
  border-radius: 5px;
  text-align: center;
}

#modal .buttons button {
  display: inline-block;
  margin-right: 15px;
}
button.details-btn {
  margin: 30px auto;
}
.details-description {
  display: inline-grid;
}
.details-description p {
  font-size: 1.4em;
}

.loader {
  margin: 250px 500px;
}
